import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import TextField from '@mui/material/TextField';
import Fab from '@mui/material/Fab';
import EditIcon from '@mui/icons-material/Edit';


export default function AdminPortal() {
  const [form, setForm] = useState({
    masterURL: ""
  });

  const params = useParams();
  const navigate = useNavigate();

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(`http://localhost:5000/AdminInfo/${params.id.toString()}`);

      if (!response.ok) {
        const message = `An error has occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
    }

    fetchData();

    return;
  }, [params.id, navigate]);


  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    const editedURL = {
      name: form.masterURL
    };

    await fetch(`http://localhost:5000/update/${params.id}`, {
      method: "POST",
     body: JSON.stringify(editedURL),
     headers: {
       'Content-Type': 'application/json'
     },
   });

    navigate("/");
  }


  return (
    <div className="adminportal">
        <header>
          <h1>Admin Portal</h1>
          <p>Primary Administrator</p>
          <p>University: State University of New York at Albany</p>
        </header>

      <form onSubmit={onSubmit}>
        <h2>Master Class Link</h2>
        <div>
          <input
              className="form-check-input"
              type="url"
              id="outlined-disabled"
              //onChange={(e) => updateForm({ deptURL: e.target.value })}
            />
        </div>
        <h2>Semester Schedule Link</h2>
        <div>
          <input
              className="form-check-input"
              type="url"
              id="outlined-disabled"
              onChange={(e) => updateForm({ masterURL: e.target.value })}
            />
        </div>
          <input
            type="submit"
            value="Submit"
          />
      </form>

      </div>
  );
}
